<?phpinclude_once('models/startup.php');include_once('models/model.php');include_once('models/template.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Извлечение статьи.if (!isset($_GET['id']))    header('Location: editor.php');$id = intval($_GET['id']);$article = $id ? articles_get($id) : false;if (!$article)    header('Location: editor.php');$title = $article['title'];$content = $article['content'];// Обработка отправки формы.if (!empty($_POST)) {    if (!empty($_POST['title']) && !empty($_POST['content'])        && articles_edit($id, $_POST['title'], $_POST['content'])    ) {        header('Location: editor.php');        die();    }    $title = $_POST['title'];    $content = $_POST['content'];    $error = true;}$menu = template('views/menu.php', ['pageId' => 0]);$view = template('views/form.php', [    'form_title' => 'Редактирование статьи',    'error' => $error,    'title' => $title,    'content' => $content,    'button_value' => 'Изменить']);$page = template('views/layout.php', [    'menu' => $menu,    'view' => $view]);// Кодировка.header('Content-type: text/html; charset=utf-8');// Вывод в шаблон.echo $page;