<?phpinclude_once('models/startup.php');include_once('models/model.php');include_once('templates/template.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Извлечение статей.$articles = articles_all();$menu = template('templates/menu.php', ['pageId' => 2]);$view = template('views/index.php', [    'type' => 'edit',    'articles' => $articles]);$page = template('templates/layout.php', [    'menu' => $menu,    'view' => $view]);// Кодировка.header('Content-type: text/html; charset=utf-8');// Вывод в шаблон.echo $page;