<?phpinclude_once('models/startup.php');include_once('models/model.php');include_once('templates/template.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Обработка отправки формы.if (!empty($_POST)) {    if (!empty($_POST['title']) && !empty($_POST['content'])        && articles_new($_POST['title'], $_POST['content']))    {        header('Location: editor.php');        die();    }    $title = $_POST['title'];    $content = $_POST['content'];    $error = true;} else {    $title = '';    $content = '';    $error = false;}$menu = template('templates/menu.php', ['pageId' => 0]);$view = template('views/form.php', [    'form_title' => 'Новая статья',    'error' => $error,    'title' => $title,    'content' => $content,    'button_value' => 'Добавить']);$page = template('templates/layout.php', [    'menu' => $menu,    'view' => $view]);// Кодировка.header('Content-type: text/html; charset=utf-8');// Вывод в шаблон.echo $page;