<?php// Языковая настройка.setlocale(LC_ALL, 'ru_RU.UTF-8');mb_internal_encoding('UTF-8');//// Автозагрузка классов//function __autoload ($name){    $type = substr($name, 0, 2);    switch($type)    {        case 'C_':            $dir = '/controllers/';            break;        case 'M_':            $dir = '/models/';            break;    }    if(isset($dir))    {        $include_path = __DIR__ . $dir . $name . '.php';        if(file_exists($include_path))        {            include_once($dir . $name . '.php');        }        else        {            die("There's no such class as " . $name);        }    }    else    {        die("There's no such class type as " . $type);    }}//// Установка контроллера//$controller = '';if(isset($_GET['c']))    $controller = $_GET['c'];switch ($controller){    case 'Article':        $c = new C_Article();        break;    default:        $c = new C_Article();}//// Установка обработчика действия//$action = (isset($_GET['a'])) ? $_GET['a'] : 'Index';//// Запрос контроллеру//$c->Request($action);