<?phpinclude_once('model/startup.php');include_once('model/M_Users.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Менеджеры.$mUsers = M_Users::GetInstance();// Очистка старых сессий.$mUsers->ClearSessions();// Текущий пользователь.$user = $mUsers->Get();// Если пользователь не зарегистрирован - отправляем на страницу регистрации.if ($user == null){	header("Location: login.php");	die();}// Может ли пользователь смотерть зарплату?if (!$mUsers->Can('VIEW_SALARY')){	die('Отказано в доступе');}// Кодировка.header('Content-type: text/html; charset=utf-8');?><!DOCTYPE html><html><head>	<title>Веб-Гуру</title>	<meta charset="utf-8" >		<link rel="stylesheet" type="text/css" media="screen" href="theme/style.css" /> </head><body>	<h1>Оклад сотрудников</h1>	<a href="index.php">Главная</a>	<ul>		<li>Иванов, 14 000</li>		<li>Петров, 28 000</li>		<li>Замуруев, 96 000</li>		<li>Семенова, 48 000</li>			</ul></body></html>