<?phprequire_once 'autoload.php';use blog\controllers\C_Application;session_start();set_exception_handler(    function ($e) {        echo 'exception: ';        echo $e->getMessage();    });$get = explode('/', $_GET['q']);$params = [];foreach ($get as $v) {    if ($v != '') {        $params[] = $v;    }}$app = new C_Application();$app->request('execute', $params);