<h1 class="text-center"><?= $form_title ?></h1><form method="POST">    <div class="form-group <?= $error['login'] ?>">        <label for="user-login" class="control-label">Имя пользователя</label>        <input type="text" class="form-control" id="user-login" name="login" value="<?= $user['login'] ?>"/>    </div>    <div class="form-group <?= $error['password'] ?>">        <label for="user-password" class="control-label">Пароль</label>        <input type="password" class="form-control" id="user-password" name="password" value="<?= $user['password'] ?>"/>        <? if('Регистрация' == $form_title): ?>            <label for="user-password2" class="control-label">Пароль еще раз</label>            <input type="password" class="form-control" id="user-password2" name="password2" value="<?= $user['password2'] ?>"/>        <? endif ?>    </div>    <? if('Регистрация' != $form_title): ?>        <input type="checkbox" id="user-remember" name="remember" />&nbsp;&nbsp;Запомнить меня    <? endif ?>    <input type="submit" class="btn btn-info" value=<?= $button_value ?>></form><p class="small text-center"><?= $error['text'] ?></p>